mapper.select:
select
i.id,i.cn_name,i.en_name,i.country_type,co.country_name as
country,i.company_regist_number,i.company_validity,i.legal_passport_code,i.legal_passport_validity,
co.area_code as
country_code,i.institution_adress,i.legal_passport_img,i.institution_contract,i.company_articles,i.business_certificate,i.institution_phone,
i.legal_name,i.business_category,i.business_license,i.industry_category,i.institution_postal_code,i.institution_email,i.institution_web_url,
i.contact_address,i.contact_people,i.contact_phone,i.audit_status,i.ext7,i.ext6,i.ext5,i.ext4,i.ext3,i.ext2,i.ext1,i.create_time,i.update_time,
i.creator,i.modifier,i.remark,i.enabled,i.institution_logo,i.develop_sales
from institution i left join country co on i.country = co.extend1 and co.language = #{language}


        and i.id=#{institutionId}


        and i.cn_name LIKE CONCAT(CONCAT('%', #{cnName}), '%')


        and i.enabled=#{enabled}


        and i.country=#{country}


        and i.audit_status=#{auditStatus}


        AND DATE_FORMAT(i.create_time,'%Y-%m-%d') >= #{startTime}


        AND DATE_FORMAT(i.create_time,'%Y-%m-%d') <= #{endTime}


        and i.enabled=#{enabled}

select i.id, i.cn_name as cnName, i.en_name as enName, i.country_type as countryType, co.country_name as country, i.company_regist_number as companyRegistNumber, i.company_validity as companyValidity, i.legal_passport_code as legalPassportCode, i.legal_passport_validity as legalPassportValidity, co.area_code as countryCode, i.institution_adress as institutionAdress, i.legal_passport_img as legalPassportImg, i.institution_contract as institutionContract, i.company_articles as companyArticles, i.business_certificate as businessCertificate, i.institution_phone as institutionPhone, i.legal_name as legalName, i.business_category as businessCategory, i.business_license as businessLicense, i.industry_category as industryCategory, i.institution_postal_code as institutionPostalCode, i.institution_email as institutionEmail, i.institution_web_url as institutionWebUrl, i.contact_address as contactAddress , i.contact_people as contactPeople, i.contact_phone as contactPhone, i.ext7, i.ext6, i.ext5, i.ext4, i.ext3, i.ext2, i.ext1, i.create_time as createTime, i.update_time as updateTime, i.creator, i.modifier, i.remark, i.institution_logo as institutionLogo, i.develop_sales as developSales, case i.audit_status when 1 then '待审核' when 2 then '审核通过' when 3 then '审核不通过' else '' end auditStatus, case i.enabled when 0 then '禁用' when 1 then '启用' else '' end enabled from institution i left join country co on i.country = co.extend1 and co.language = #{language}
and i.id=#{institutionId}
and i.cn_name LIKE CONCAT(CONCAT('%', #{cnName}), '%')
and i.enabled=#{enabled}
and i.country=#{country}
and i.audit_status=#{auditStatus}
AND DATE_FORMAT(i.create_time,'%Y-%m-%d') >= #{startTime}
AND DATE_FORMAT(i.create_time,'%Y-%m-%d') <= #{endTime}
and i.enabled=#{enabled}
