mapper.select:
select count(*)
from institution_product
where institution_id = #{institutionId}

select id
from institution_product
where institution_id = #{institutionId}

SELECT ip.institution_id AS institutionId, ip.institution_name AS institutionName, ip.product_id AS productId, ip.product_abbrev AS productAbbrev, ic.channel_id AS channelId, ic.ins_pro_id AS insProId, ic.create_time AS createTime, ic.update_time AS updateTime, ic.creator AS creator, ic.modifier AS modifier, ic.remark AS remark, concat(pt.NAME, '-', p.currency) AS productName, pt.NAME AS payType, pt.extend1 AS payTypeId, p.trade_direction AS transType FROM institution_product ip LEFT JOIN institution_channel ic ON ip.id = ic.ins_pro_id LEFT JOIN product p ON ip.product_id = p.id LEFT JOIN pay_type pt ON p.pay_type = pt.extend1 LEFT JOIN merchant m ON m.institution_id = ip.institution_id AND pt.language = #{language}
AND ip.institution_id = #{institutionId}
AND m.id = #{merchantId}

select DISTINCT
institution_id,institution_name,create_time,creator,update_time,modifier
from institution_product


        AND institution_id=#{institutionCode}


        AND institution_name LIKE CONCAT(CONCAT('%', #{institutionName}), '%')


        AND DATE_FORMAT(create_time,'%Y-%m-%d') >= #{startTime}


        AND DATE_FORMAT(create_time,'%Y-%m-%d') <= #{endTime}

SELECT ip.institution_id AS institutionId, ip.institution_name AS institutionName, ip.product_id AS productId, ip.product_abbrev AS productAbbrev, ip.create_time AS createTime, ip.update_time AS updateTime, ip.creator AS creator, ip.modifier AS modifier, ip.remark AS remark, concat(pt.NAME, '-', p.currency) AS productName, pt.NAME AS payType, pt.extend1 AS payTypeId, p.trade_direction AS transType FROM institution_product ip LEFT JOIN product p ON ip.product_id = p.id LEFT JOIN pay_type pt ON p.pay_type = pt.extend1 AND pt.language = #{language}
AND ip.institution_id=#{institutionId}
AND m.merchant_id= #{merchantId}
AND p.trade_direction=#{transType}
AND pt.extend1=#{payType}

SELECT ip.institution_id as institutionId, ip.institution_name as institutionName, c.channel_code as channelCode, c.channel_cn_name as channelCnName, c.currency as currency, c.support_refund_state as supportRefundState, c.refunding_is_return_fee as refundingIsReturnFee, c.sort as sort, c.enabled as enabled, b.id as bankID, b.bank_code as bankCode, b.bank_name as bankName, b.bank_country as bankCountry, b.bank_currency as bankCurrency, b.issuer_id as bankIssuerId, ip.create_time FROM institution_product ip left join institution_channel ic on ic.ins_pro_id=ip.id left join channel c on c.id=ic.channel_id LEFT JOIN channel_bank cb on cb.channel_id = c.id left join bank b on cb.bank_id = b.id
AND ip.institution_id = #{institutionId}
AND c.channel_code = #{channelCode}
AND c.channel_cn_name LIKE CONCAT(CONCAT('%', #{channelCnName}), '%')
AND c.currency = #{currency}
and c.enabled=#{enabled}
AND c.support_refund_state = #{supportRefundState}
AND c.refunding_is_return_fee = #{refundingIsReturnFee}
