mapper.select:
select

,m.cn_name as merchant_name
from settle_check_account sca left join merchant m on sca.merchant_id = m.id


		AND currency = #{currency}


		AND merchant_id = #{merchantId}


		AND DATE_FORMAT(check_time,'%Y-%m-%d') >= #{startDate}


		AND DATE_FORMAT(check_time,'%Y-%m-%d') <= #{endDate}


		AND DATE_FORMAT(check_time,'%Y-%m-%d') = DATE_FORMAT(#{checkDate},'%Y-%m-%d')

select
id, DATE_FORMAT(check_time,'%Y-%m-%d') as check_time, currency, amount, fee, refund_order_fee,initial_amount, final_amount, create_time, merchant_id,count,
update_time, creator, modifier, remark
from settle_check_account


		AND currency = #{currency}


		AND merchant_id = #{merchantId}


		AND DATE_FORMAT(check_time,'%Y-%m-%d') >= #{startDate}


		AND DATE_FORMAT(check_time,'%Y-%m-%d') <= #{endDate}


		AND DATE_FORMAT(check_time,'%Y-%m-%d') = DATE_FORMAT(#{checkDate},'%Y-%m-%d')


order by check_time desc
